parameters.jsCode:
const approvalData = $('profile_extract').first().json;

// Get profile data from LinkedIn Profile Scraper node 
const profileData = $('Linkedin_Profile_Analyser').first().json['output']['profile_data'];

// Initialize conversation context
const conversationContext = {
  // Conversation tracking
  conversation_id: `conv_${Date.now()}`,
  exchange_count: 0,
  max_exchanges: 2,
  status: "initiating",
  next_action: "send_initial_message",

  // LinkedIn target info
  linkedin_url: approvalData.linkedin_url,

  // Event details for messaging context
  event_data: {
    event_type: approvalData.event_data.event_type,
    event_topic: approvalData.event_data.event_topic,
    event_date: approvalData.event_data.event_date,
    event_location: approvalData.event_data.event_location,
    audience: approvalData.event_data.audience
  },

  // Speaker profile for personalization
  profile_data: {
    full_name: profileData.full_name,
    current_title: profileData.current_title,  
    current_company: profileData.current_company,
    headline: profileData.headline,
    about_section: profileData.about_section,
    skills: profileData.skills,
    industry: profileData.industry,
    certifications: profileData.certifications
  },

  // Slack user info for notifications
  slack_user_id: approvalData.user_id,
  slack_user_name: approvalData.user_name,

  // Metadata
  timestamp: new Date().toISOString()
};

return [{ json: conversationContext }];

// Process Slack button interaction payload
const body = items[0].json.body;
let interactionData;

// Parse the form-encoded payload from Slack
try {
  if (body.payload) {
    interactionData = JSON.parse(body.payload);
  } else {
    interactionData = body;
  }
} catch (error) {
  return [{
    json: {
      error: "Failed to parse Slack interaction",
      raw_body: body
    }
  }];
}

// Check if this is an approve button click
if (interactionData.type !== "block_actions" || 
    !interactionData.actions || 
    interactionData.actions[0].action_id !== "approve_profile") {

  // Not an approve action, ignore
  return [];
}

// Extract the approval data
const action = interactionData.actions[0];
const actionValue = JSON.parse(action.value);
const user = interactionData.user;

const approvalData = {
  // From button value
  linkedin_url: actionValue.linkedin_url,
  profile_index: actionValue.profile_index,
  event_data: actionValue.event_data, // If you included it in Step 1

  // From Slack interaction
  user_id: user.id,
  user_name: user.name,
  channel_id: interactionData.channel?.id,
  message_ts: interactionData.message?.ts,
  trigger_id: interactionData.trigger_id,

  // Metadata
  timestamp: new Date().toISOString(),
  action_type: "approve_speaker"
};

return [{ json: approvalData }];

// Initialize conversation polling after initial LinkedIn message sent
const conversationContext = items[0].json;

// Create polling configuration
const pollingConfig = {
  conversation_id: conversationContext.conversation_id,
  linkedin_url: conversationContext.linkedin_url,
  event_data: conversationContext.event_data,
  profile_data: conversationContext.profile_data,
  slack_user_id: conversationContext.slack_user_id,
  chat_id: $('HTTP Request2').first().json['chat_id'],

  // Polling state
  exchange_count: 0,
  max_exchanges: 2,
  polling_interval: 5000, // 5 seconds
  last_checked_timestamp: new Date().toISOString(),
  conversation_active: true,

  // Message tracking
  last_message_from: "system", // "system", "user", "speaker"
  last_message_content: "",
  messages_history: []
};

return [{ json: pollingConfig }];

// Process Unipile conversation response with robust message ordering
if (!items || items.length === 0) {
  return [{ json: { error: "No input data received" } }];
}

const inputData = items[0].json;
const messagesList = inputData.items || [];
const conversationId = inputData.conversation_id || `conv_${Date.now()}`;
const maxExchanges = 2;

// Identify participant IDs
let ourAttendeeId = "";
let speakerAttendeeId = "";

messagesList.forEach(message => {
  if (message.is_sender === 1) {
    ourAttendeeId = message.sender_attendee_id;
  } else if (message.is_sender === 0) {
    speakerAttendeeId = message.sender_attendee_id;
  }
});

// **KEY FIX**: Sort messages by timestamp to get correct chronological order
const sortedMessages = messagesList.sort((a, b) => 
  new Date(a.timestamp) - new Date(b.timestamp)
);

// Get the ACTUAL last message (chronologically)
const lastMessage = sortedMessages.length > 0 ? sortedMessages[sortedMessages.length - 1] : null;

// **CORRECT LOGIC**: Check is_sender field of the chronologically last message
let lastMessageFromSpeaker = false;
if (lastMessage) {
  lastMessageFromSpeaker = (lastMessage.is_sender === 0); // 0 means from speaker
}

// Count messages for exchange calculation
let agentMessagesCount = 0;
let speakerMessagesCount = 0;

sortedMessages.forEach(message => {
  if (message.sender_attendee_id === ourAttendeeId) {
    agentMessagesCount++;
  } else if (message.sender_attendee_id === speakerAttendeeId) {
    speakerMessagesCount++;
  }
});

// Calculate completed exchanges
const currentExchangeCount = Math.max(0, Math.min(agentMessagesCount - 1, speakerMessagesCount));

// **ROBUST STATUS DETERMINATION**
let conversationStatus;
let shouldGenerateReply = false;
const conversationActive = currentExchangeCount < maxExchanges;

if (lastMessageFromSpeaker && conversationActive) {
  conversationStatus = "reply_needed";
  shouldGenerateReply = true;
} else if (!lastMessageFromSpeaker && conversationActive) {
  conversationStatus = "waiting_for_speaker";
} else if (currentExchangeCount >= maxExchanges) {
  conversationStatus = "completed";
} else {
  conversationStatus = "waiting";
}

// Build conversation history in chronological order
const conversationHistory = sortedMessages.map(msg => ({
  sender: msg.sender_attendee_id === ourAttendeeId ? "agent" : "speaker",
  message: msg.text,
  timestamp: msg.timestamp,
  is_sender: msg.is_sender
}));

return [{
  json: {
    // Conversation tracking
    conversation_id: conversationId,
    exchange_count: currentExchangeCount,
    max_exchanges: maxExchanges,

    // Status and actions  
    status: conversationStatus,
    should_generate_reply: shouldGenerateReply,
    conversation_active: conversationActive,
    next_action: shouldGenerateReply ? "generate_ai_reply" : 
                (conversationActive ? "continue_waiting" : "send_final_notification"),

    // Message analysis
    last_message_from_speaker: lastMessageFromSpeaker,
    last_message: lastMessage || {},

    // Participant info
    our_attendee_id: ourAttendeeId,
    speaker_attendee_id: speakerAttendeeId,

    // Full context
    conversation_history: conversationHistory,
    messages_total: sortedMessages.length,
    agent_messages_count: agentMessagesCount,
    speaker_messages_count: speakerMessagesCount,

    // Debug info
    last_message_is_sender_value: lastMessage ? lastMessage.is_sender : null,
    last_message_text: lastMessage ? lastMessage.text : "",
    last_checked_timestamp: new Date().toISOString()
  }
}];

// Update exchange counter after sending AI reply - PRESERVE ALL DATA
const conversationData = $('Code2').first().json;
const aiReply = $('AI Agent1').first().json.output;
const messageResponse = items[0].json; // From HTTP Request3

// Increment exchange count since we just sent a response
const updatedExchangeCount = conversationData.exchange_count + 1;
const maxExchanges = conversationData.max_exchanges || 2;

// Determine if conversation should continue
const shouldContinue = updatedExchangeCount < maxExchanges;
const conversationCompleted = updatedExchangeCount >= maxExchanges;

// **PRESERVE ALL ORIGINAL DATA** from Code2
const updatedContext = {
  // Preserve ALL original data from Code2
  ...conversationData,

  // Override specific fields that need updating
  exchange_count: updatedExchangeCount,
  max_exchanges: maxExchanges,
  conversation_active: shouldContinue,
  status: conversationCompleted ? "completed" : "waiting_for_response",
  should_continue: shouldContinue,

  // Add new tracking data
  last_ai_message: aiReply,
  last_message_sent_at: new Date().toISOString(),
  last_checked_timestamp: new Date().toISOString(),

  // Preserve chat_id from Code1
  chat_id: $('Code1').first().json.chat_id,
  polling_interval: 5000
};

return [{ json: updatedContext }];

// Final Conversation Analyzer - PRESERVE ALL ORIGINAL DATA
const finalData = items[0].json;

// **CRITICAL: Get missing data from earlier nodes if not present**
const eventData = finalData.event_data || $('Code1').first().json.event_data;
const profileData = finalData.profile_data || $('Code2').first().json.profile_data;
const linkedinUrl = finalData.linkedin_url || $('Code2').first().json.linkedin_url;

const conversationHistory = finalData.conversation_history || [];

// Extract speaker messages safely
const speakerMessages = conversationHistory
  .filter(msg => msg && msg.sender === "speaker")
  .map(msg => (msg.message || "").toLowerCase())
  .join(" ");

// Simple keyword-based outcome determination
const positiveKeywords = ["interested", "yes", "tell me more", "when", "calendar", "schedule", "sounds good", "great", "perfect"];
const negativeKeywords = ["not interested", "busy", "no", "can't", "unable", "not available", "sorry"];

const hasPositive = positiveKeywords.some(keyword => speakerMessages.includes(keyword));
const hasNegative = negativeKeywords.some(keyword => speakerMessages.includes(keyword));

let finalOutcome = "INCONCLUSIVE";
let outcomeReason = "Reached maximum exchanges without clear outcome";

if (hasPositive && !hasNegative) {
  finalOutcome = "SUCCESSFUL";
  outcomeReason = "Speaker showed interest and engagement";
} else if (hasNegative) {
  finalOutcome = "UNSUCCESSFUL";
  outcomeReason = "Speaker declined or showed disinterest";
} else if (finalData.exchange_count >= (finalData.max_exchanges || 2)) {
  finalOutcome = "NEEDS_FOLLOW_UP";
  outcomeReason = "Speaker remained engaged but needs human follow-up";
}

const lastMessageText = (finalData.last_message && finalData.last_message.text) || 
                       (finalData.last_message_text) || 
                       "No final message";

return [{
  json: {
    // **PRESERVE ALL ORIGINAL FIELDS**
    ...finalData,

    // **ENSURE CRITICAL DATA IS PRESENT**
    event_data: eventData,
    profile_data: profileData, 
    linkedin_url: linkedinUrl,

    // Add analysis results
    final_outcome: finalOutcome,
    outcome_reason: outcomeReason,
    conversation_completed_at: new Date().toISOString(),
    total_exchanges: finalData.exchange_count || 0,
    conversation_summary: `${finalData.exchange_count || 0} exchanges completed. Last message: "${lastMessageText}"`
  }
}];

// Final Slack Notification - Get LinkedIn URL from Code node
const result = items[0].json;

// Extract data safely from current result
const eventType = (result.event_data && result.event_data.event_type) ;
const eventTopic = (result.event_data && result.event_data.event_topic) ;
const slackUserId = result.slack_user_id || "U1234567890";
const profileName = (result.profile_data && result.profile_data.full_name) ;
const finalOutcome = result.final_outcome || "INCONCLUSIVE";
const outcomeEmoji = finalOutcome === "SUCCESSFUL" ? "‚úÖ" : 
                    finalOutcome === "UNSUCCESSFUL" ? "‚ùå" : "üîÑ";

// **CRITICAL: Get LinkedIn URL from Code node as specified**
const linkedinUrl = $('Code').first().json.linkedin_url;

// Validate LinkedIn URL is present (as you specified it can't be unavailable)
if (!linkedinUrl) {
  console.error("LinkedIn URL not found in Code node!");
  return [{
    json: {
      channel: slackUserId,
      text: `ERROR: LinkedIn URL missing from Code node for ${profileName}`,
      blocksUi: JSON.stringify({ blocks: [] })
    }
  }];
}

// Create blocks as JSON object first (same style as your reference)
const blocks = [
  {
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*${outcomeEmoji} LinkedIn Outreach Result for your ${eventType} event on ${eventTopic}*`
    }
  },
  {
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*Speaker Profile:* <${linkedinUrl}>`
    }
  },
  {
    type: "divider"
  }
];

// Add outcome details in fields format
blocks.push({
  type: "section",
  fields: [
    {
      type: "mrkdwn",
      text: `*Outcome:* ${finalOutcome}`
    },
    {
      type: "mrkdwn",
      text: `*Exchanges:* ${result.total_exchanges || 0}/3`
    }
  ]
});

// Add conversation summary if available
if (result.outcome_reason) {
  blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*Summary:* ${result.outcome_reason}`
    }
  });
}

// Add conversation details if available
if (result.conversation_summary) {
  blocks.push({
    type: "divider"
  });
  blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*Conversation Details:*\n${result.conversation_summary}`
    }
  });
}

// Add footer context (like your reference example)
blocks.push({
  type: "context",
  elements: [
    {
      type: "mrkdwn",
      text: `üìä Total exchanges: ${result.total_exchanges || 0} | ‚è∞ Completed: ${new Date().toLocaleString()}`
    }
  ]
});

// Convert to the string format that n8n expects (exactly like your reference)
const blocksUiString = JSON.stringify({ blocks: blocks });

return [{
  json: {
    channel: slackUserId,
    blocksUi: blocksUiString,
    text: `LinkedIn outreach ${finalOutcome}: ${profileName} for your ${eventType} event on ${eventTopic}`
  }
}];
